				document.addEventListener("DOMContentLoaded", function() {
    // Находим все элементы меню с дочерними элементами
    let menuItems = document.querySelectorAll('.nav-item');

    menuItems.forEach(function(menuItem) {
        // Ищем все стрелки (элементы с классом .arrow) в каждом пункте меню
        let arrows = menuItem.querySelectorAll('.arrow');
        
        if (arrows.length > 1) {
            // Оставляем только одну стрелку, остальные удаляем
            for (let i = 1; i < arrows.length; i++) {
                arrows[i].remove();
            }
        }
    });
});

