/* Основной контейнер меню */
.menu {
    position: relative;
    z-index: 10;
}

/* Бургер-кнопка */
.menu__burger {
    position: relative; /* Статичная кнопка */
    width: 40px;
    height: 30px;
    cursor: pointer;
    z-index: 20;
    display: flex;
    align-items: center;
    justify-content: center;
    background: none; /* Убираем фон */
    border: none; /* Убираем рамку */
    outline: none; /* Убираем фокусную рамку */
    margin: 10px 0; /* Расстояние сверху и снизу */
}

.menu__burger span,
.menu__burger:before,
.menu__burger:after {
    position: absolute;
    width: 73%;
    height: 2px;
    background: #333; /* Цвет линий */
    border-radius: 2px;
    transition: 0.3s ease; /* Плавность анимации */
    content: '';
}

.menu__burger:before {
    top: -7px; /* Располагаем первую линию выше */
}

.menu__burger:after {
    top: 7px; /* Располагаем третью линию ниже */
}

.menu__burger span {
    top: 0; /* Центральная линия */
}

/* Анимация для активного состояния */
.menu__burger.active span {
    transform: scale(0); /* Прячем центральную линию */
    opacity: 0;
}

.menu__burger.active:before {
    transform: rotate(45deg) translate(5px, 5px);
}

.menu__burger.active:after {
    transform: rotate(-45deg) translate(5px, -5px);
}

/* Контейнер меню */
.menu__wrap {
    position: fixed; /* Меню остаётся фиксированным */
    top: 0;
    right: -100%; /* Скрыто за правым краем экрана */
    width: 300px; /* Ширина выдвигающегося меню */
    height: 100%;
    background: #fff;
    box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    transition: 0.5s ease;
    z-index: 15;
}

.menu__wrap.active {
    right: 0; /* Показывается на экране */
}

/* Внутренности меню */
.menu__inner {
    padding: 20px;
    display: flex;
    flex-direction: column;
    max-height: 100%;
    overflow-y: auto;
}

.menu__menu {
    list-style: none;
    margin: 0;
    padding: 0;
}

.menu__li {
    margin-bottom: 15px;
}

.menu__li a {
    color: #444;
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    display: block;
    padding: 10px 0;
    transition: 0.3s ease;
}

.menu__li a:hover {
    color: #236cc1;
}

/* Адаптив для мобильных */
@media screen and (max-width: 768px) {
    .menu__burger {
        display: block;
    }
}

		
